mapper.insert:
INSERT INTO MYLIST
(
  user_id
, name
, link_url
, remark
, res_id
)
VALUES
(
   #{userId}
 , #{name}
 , #{linkUrl}
 , #{remark}
 , #{resId}
)

mapper.update:
UPDATE MYLIST
SET name = #{name}
  , link_url = #{linkUrl}
  , remark = #{remark}
WHERE id = #{id}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM MYLIST
WHERE id = #{id}
  AND user_id = #{userId}
